// Import Libraries
import React, { useState } from "react";
import Switch from "react-switch";

const CaptionDisplay = (props) => {
  const [checked, setChecked] = useState(false);

  const onClickHandler = () => {
    setChecked(!checked);
  };

  return (
    <div class="flex flex-col w-40 h-64 bg-transparent rounded-3xl">
      <div class="flex flex-row h-1/4 justify-center align-middle p-3">
        <text class=" text-custom-light-blue text-sm">Captions Exist?</text>
        <label>
          <Switch onChange={onClickHandler} checked={checked} />
        </label>
      </div>
      <div class="h-3/4 bg-green-400 rounded-3xl rounded-t-none border-2 border-custom-light-blue">
        <text>Submit for Processing</text>
      </div>
    </div>
  );
};

export default CaptionDisplay;
