    setData({
      ...data,
      data: val,
    });
  };

  const onClickHandler = () => {
    console.log("Start Recording");
  };

  return (
    <div class="w-3/4 h-14 p-3 flex flex-row border-white border-2 bg-transparent rounded-3xl justify-items-start items-center text-white">
      <text>Hello</text>
    </div>
  );
};

export default Answers;
