// Import Libraries
import React, { useState } from "react";

const Answers = (props) => {
  const [data, setData] = useState({
    question: "",
  });

  const onChangeHandler = (val) => {
    setData({
      ...data,
      data: val,
    });
  };

  const onClickHandler = () => {
    console.log("Start Recording");
  };

  return (
    <div class="w-3/4 h-14 p-3 flex flex-row border-white border-2 bg-transparent rounded-3xl justify-items-start items-center">
      <i class="fas fa-search text-white m-2"></i>
      <input
        class="w-5/6 bg-transparent m-0 text-white rounded-xl focus:outline-none placeholder-custom-light-blue"
        type="search"
        placeholder={props.placeholder}
        onChange={(val) => onChangeHandler(val)}
        autoFocus={false}
      />
      <button
        class=" ml-4 p-2 text-black rounded-3xl text-sm hover:text-white hover:bg-custom-light-blue  
          active:bg-blue-700 self-center"
        onClick={onClickHandler}
      >
        <i class="fas fa-microphone"></i>
      </button>
    </div>
  );
};

export default Answers;
