// Import Libraries
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React, { useState } from "react";

const MainSearchBar = (props) => {
  const [data, setData] = useState({
    question: "",
  });

  const onChangeHandler = (val) => {
    setData({
      ...data,
      data: val,
    });
  };

  const onSubmitHandler = () => {
    console.log("works");
  };

  return (
    <div class="w-1/2 h-14 p-3 flex flex-row bg-gradient-to-r from-transparent via-blue-700 to-pink-200 rounded-3xl border-white justify-items-start items-center">
      {/* <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" color="white" /> */}
      <input
        class="w-4/5 bg-transparent m-0 text-white rounded-xl focus:outline-none"
        type="search"
        placeholder={props.placeholder}
        onChange={(val) => onChangeHandler(val)}
        autoFocus={false}
      />
      <button
        class="ml-3 p-2 text-black bg-blue-400 rounded-3xl text-sm hover:text-white hover:bg-blue-500  active:bg-blue-700 border-double border-4 border-light-blue-500"
        onClick={onSubmitHandler}
      >
        Submit
      </button>
    </div>
  );
};

export default MainSearchBar;
